            <div className="card">
                <div className="card_main">
                    <div className="card_inner">
                        <p className="card_name"><span>OUR</span> COUNTRY</p>
                        <p className="card_total card_small">INDIA</p>
                    </div>
                    <div className="card_inner">
                        <p className="card_name"><span>OUR</span> COUNTRY</p>
                        <p className="card_total card_small">INDIA</p>
                    </div>
                    <div className="card_inner">
                        <p className="card_name"><span>OUR</span> COUNTRY</p>
                        <p className="card_total card_small">INDIA</p>
                    </div>
                </div>
            </div>
            
            

        </>
    );
}
export default Covid;
